import {makeStyles} from '@material-ui/core/styles';

export default makeStyles({
    media:{
        height:250,
    },
    border:{
        border:'solid',
    },
    fullHeightCard:{
        height:'100%',
    },
    card:{
        display:'flex',
        flexDirection:'column',
        justifyContent:'space-between',
        borderBottom:'10px solid red',

    },
    activeCard:{
        borderBottom:'10px solid red',
    },
    grid:{
        display:'flex',
    },
    details:{
        display:'flex',
        justifyContent:'space-between',
        margin:'20px',

    },
    title:{
        padding:''
    }

});
