import React from 'react';
import {Grid,Grow,Typography} from '@material-ui/core';
import NewsCardss from '../NewsCard/NewsCardss';

import useStyles from './style.js';

const infoCards = [
    { color: '#00838f', title: 'Latest News', text: 'Give me the latest news' },
    { color: '#1565c0', title: 'News by Categories', info: 'Business, General, Health, Sports, Technology', text: 'Give me the latest Technology news',image:'http://wadr.org/app/cover_image/Health-Insurance-Quotes.jpg' },
    { color: '#4527a0', title: 'News by Terms', info: 'Bitcoin, PlayStation 5, Smartphones, Donald Trump...', text: 'What\'s up with PlayStation 5',image:'https://thumbor.forbes.com/thumbor/960x0/https%3A%2F%2Fspecials-images.forbesimg.com%2Fdam%2Fimageserve%2F908633080%2F960x0.jpg%3Ffit%3Dscale' },
    { color: '#283593', title: 'News by Sources', info: 'CNN, Wired, BBC News, Time, IGN, Buzzfeed, ABC News...', text: 'Give me  news from CNN' ,image:'https://ichef.bbci.co.uk/images/ic/1200x675/p07jbsw9.jpg'},
  ];
const NewsCards = ({Articles}) => {
    const classes=useStyles();
    if(!Articles.length)
    {
        return(
            <Grow in>
            < Grid className={classes.container} container alignItems="stretch" spacing={3}>
                {infoCards.map((infoCard)=>{
                    return(
                        <Grid item xs={12} sm={6} md={4} lg={3} className={classes.infoCard}>
                            <div className={classes.card}style={{backgroundColor:infoCard.color}}>
                                <Typography variant="h5">{infoCard.title}</Typography>
                                {
                                infoCard.info?(<Typography variant="h6">
                                    <strong>
                                        {infoCard.title.split(' ')[2]};
                                        <br/>
                                        {infoCard.info}

                                    </strong>
                                    </Typography>):null
                                }
                                <Typography variant="h6">Try Saying:<br/><i>{infoCard.text}</i></Typography>

                            </div>
                            <div className={classes}>
                                <img src={infoCard.image}/>

                            </div>

                            </Grid>
                    )
                })
    }
            </Grid>
            </Grow>
        )
    }
    return (
        <Grow in>
            <Grid className={classes.container} container alignItems="stretch" spacing={3}>
            {Articles.map((article,i)=>{
                return (
                    <Grid item xs={12} sm={6} md={4} lg={3} style={{display:'flex'}}>
                    <NewsCardss article={article} i={i}/>
                    </Grid>
                )  

            })}

            </Grid>
        </Grow>
    );
}

export default NewsCards;
