import {makeStyles} from '@material-ui/core/styles';

export default makeStyles({
    container:{
        padding:'0 6%',
        width:'100%',
        margin:0,

    },
    card:{
        display:'flex',
        flexDirection:'column',
        justifyContent:'center',
        alignItems:'center',
        width:'100%',
        height:'45vh',
        padding:'10%',
        borderRadius:10,
        color:'white',

    },
    infoCard:{
        display:'flex',
        alignContent
    }
});
